import React from "react"
import { Link } from "react-router-dom"
import { useState, useEffect } from "react"
import axios from "axios"
import { Navigate } from "react-router-dom"

function Register(props) {
  const image = require("../gifs/shoe.gif")
  const URL = "http://localhost:3001"
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [redirect, setRedirect] = useState(false)

  const UserInfo = React.createContext({})

  const registerUser = (e) => {
    e.preventDefault()
    const res = axios
      .post(`${URL}/add-user/`, { email, password })
      .then((res) => {
        console.log(res.data)
        setEmail("")
        setPassword("")
      })
      .catch((error) => console.log(error))
  }
  // if (redirect) {
  //   return <Navigate to={"/landing"} />
  // }

  return (
    <div className="landing-background">
      <div className="App-header">
        <h1 className="logo">Lucidity</h1>
        <div>
          <Link className="nav" to="/landing">
            logout
          </Link>
          <Link className="nav" to="/">
            home
          </Link>
        </div>
      </div>
      <form className="login-container">
        <img className="landing-image" src={image}></img>
        <div className="login-background">
          <h3 className="email">Email</h3>
          <input
            className="email-input"
            type="email"
            placeholder="Email Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          ></input>
          <br />
          <h3 className="password">Password</h3>
          <input
            className="password-input"
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          ></input>
          <br />
          <button className="register-btn" onClick={(e) => registerUser}>
            Register
          </button>
        </div>
      </form>
    </div>
  )
}

export default Register
