import React from "react"
import { Link } from "react-router-dom"
import { useState, useContext } from "react"
import axios from "axios"
import { useNavigate } from "react-router-dom"

function Register(props) {
  const image = require("../gifs/shoe.gif")
  const URL = "http://localhost:3001"
  let navigate = useNavigate()

  //createContext is code sourced online
  const UserContext = React.createContext({})
  const user = useContext(UserContext)

  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [newReg, setNewReg] = useState(false)

  const register = async (e) => {
    e.preventDefault()
    const creds = { email, password }
    const res = await axios
      .post(`${URL}/new-user`, { email, password })
      .then((res) => {
        setEmail("")
        setPassword("")
        setNewReg(true)
        navigate("/landing")

        if (newReg === false) {
          return alert("Email already exists")
        } else {
          setNewReg(false)
          navigate("/landing")
        }
      })
  }

  return (
    <div className="landing-background">
      <div className="App-header">
        <h1 className="landing-logo">Lucidity</h1>
      </div>
      <div className="login-container">
        <img className="landing-image" src={image}></img>
        <form className="login-background" onSubmit={(e) => register(e)}>
          <h3 className="email">Email</h3>
          <input
            className="email-input"
            type="email"
            name="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          ></input>
          <br />
          <h3 className="password">Password</h3>
          <input
            className="password-input"
            type="password"
            name="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          ></input>
          <br />
          <button type="submit" className="register-btn">
            Register
          </button>
        </form>
      </div>
    </div>
  )
}
export default Register
