import React from "react"
import { useState, useEffect } from "react"
import axios from "axios"
import { useParams } from "react-router-dom"

const URL = "http://localhost:3001"

useEffect(() => {
  const deleteNote = async () => {
    const res = await axios
      .delete(`${URL}/notes/delete/`, {
        params: { id: notes },
      })
      .then((res) => {
        console(res)
      })
  }
  deleteNote()
})

function Note(props) {
  const [notes, setNotes] = useState([])
  const [newNote, setNewNote] = useState("")

  useEffect(() => {
    const getNotes = async () => {
      const res = await axios.get(`${URL}/notes`)
      console.log(res)
      setNotes(res.data)
    }
    getNotes()
  }, [])

  useEffect(() => {
    const deleteNote = async () => {
      const res = await axios
        .delete(`${URL}/notes/delete/`, {
          params: { id: notes },
        })
        .then((res) => {
          console(res)
        })
    }
    deleteNote()
  })

  useEffect(() => {
    const noteComplete = async () => {
      const res = await axios
        .put(`${URL}/notes/done/`)
        .then((res) => res.json())

      setNotes((notes) =>
        notes.map((notez) => {
          if (notez._id === notez._id) {
            notez.complete = res.complete
          }
          return notez
        })
      )
    }
    noteComplete()
  })

  return (
    <div className="note-wrapper">
      <h3>write it down</h3>
      <div className="note-container">
        {notes.map((notez) => (
          <div
            className={"note" + (notez.complete ? "is-complete" : "")}
            key={notez._id}
            // onClick={() => noteComplete(notez._id)}
          >
            <div className="checkbox"> Check</div>
            <div className="text">{notez.text}</div>
            <button className="remove-note" onClick={() => deleteNote(notes)}>
              X
            </button>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Note
