import React from "react"
import { useState, useEffect } from "react"
import axios from "axios"
import NoteText from "./NoteText"
import { deleteNote, updateNotes } from "../../../backend/controllers"

const URL = "http://localhost:3001"

function Note(props) {
  const [text, setText] = useState("")
  const [note, setNote] = useState([])
  const [input, setInput] = useState("")

  useEffect(() => {
    const getNotes = async () => {
      const res = await axios.get(`${URL}/get-todo`)
      setNote(res.data.notes)
      console.log(res.data.notes)
    }
    getNotes()
  }, [])

  const deleteTodo = () => {}

  return (
    <div className="note-wrapper">
      <h3 className="write">write it down</h3>
      <button className="add">+</button>
      <input
        className="input-text"
        placeholder={"write here"}
        value={text}
        onChange={(e) => setText(e.target.value)}
      ></input>
      <div className="note-container">
        <div className="notes">
          <div className="text">
            {note.map((text) => (
              <NoteText
                key={text._id}
                text={text.text}
                remove={() => deleteNote(text._id)}
                update={() => updateNotes(text._id, text.text)}
              />
            ))}
          </div>
          <button className="remove-note">X</button>
        </div>
      </div>
    </div>
  )
}

export default Note
