import React, { useEffect, useState } from "react"
import bike from "../gifs/bike.gif"
import breezy from "../gifs/breezy-night.gif"

const GifsSlideShow = ({ imgs }) => {
  const [image, setImage] = useState(0)

  useEffect(() => {
    setImage(0)
  }, [])

  const nextImg = () => {
    if (image === imgs.length - 1) {
      setImage(0)
    } else {
      setImage(image + 1)
    }
  }

  const prevImg = () => {
    if (image === 0) {
      setImage(imgs.length - 1)
    } else {
      setImage(image - 1)
    }
  }

  return (
    <div className="slideshow">
      <img className="mainImg" src={imgs[image]}></img>
      <div className="arrows">
        <button className="left-arrow" onClick={prevImg}>
          ◀
        </button>
        <button className="right-arrow" onClick={nextImg}>
          ▶
        </button>
      </div>
    </div>
  )
}

function GIFs(props) {
  return (
    <div>
      <GifsSlideShow
        imgs={[
          bike,
          breezy,
          commute,
          creamer,
          crescent,
          flowers,
          mirror,
          night,
          nightdrive,
          rose,
          shoe,
          shoe2,
          tea,
        ]}
      />
    </div>
  )
}

export default GIFs
