import React from "react"
import { useState, useEffect } from "react"
import axios from "axios"
import { useParams } from "react-router-dom"

const URL = "http://localhost:3001"

function Note(props) {
  const [notes, setNotes] = useState([])
  const [newNote, setNewNote] = useState("")

  useEffect(() => {
    const getNotes = async () => {
      const res = await axios.get(`${URL}/notes`)
      console.log(res)
      setNotes(res.data)
    }
    getNotes()
  }, [])

  const noteComplete = async (id) => {
    const res = await axios
      .put(`${URL}/notes/done/` + id)
      .then((res) => res.json())
    console.log(res.data.id)
    setNotes((notes) =>
      notes.map((notez) => {
        if (notez.id === res.id) {
          notez.complete = res.complete
        }
        return notez
      })
    )
  }
  noteComplete()

  return (
    <div className="note-wrapper">
      <h3>write it down</h3>
      <div className="note-container">
        {notes.map((notez) => (
          <div
            className={"note" + (notez.complete ? "is-complete" : "")}
            key={notez._id}
            onClick={() => noteComplete(notez.id)}
          >
            <div className="checkbox"> Check</div>
            <div className="text">{notez.text}</div>
            <div className="remove-note">X</div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Note
