  useEffect(() => {
    const deleteNote = async (id) => {
      const res = await axios
        .delete(`${URL}/notes/delete/` + id)
        .then((res) => res.json())
      setNotes((notes) => notes.filter((notez) => notez._id !== res.data._id))
    }
    deleteNote()
  })

  return (
    <div className="note-wrapper">
      <h3>write it down</h3>
